package concept;

public class Assignmentoperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// =,+=,-=,*=,/=
		int x= 10;
		x+=10; //x=x+10
		System.out.println(x);
		
		x-=10; // x=x-10 
		System.out.println(x);
		
		x*=2; //x=x*2
		System.out.println(x);
		
		x/=5; //x=x/5
		System.out.println(x);
	}

}
