package concept;

public class operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 26;
		int sum = a+b;
		int subs= a-b;
		int multi= a*b;
		float div = (float)b/a; 
		System.out.println(sum);
		System.out.println(subs);
		System.out.println(multi);
		System.out.println(div);
		
		
	}

}
